    [Flags]
    public enum ResourceFlags : int {	
        
        /// <summary>	
        /// <dd> <p> No options are specified. </p> </dd>	
        /// </summary>	
        /// <include file='.\..\Documentation\CodeComments.xml' path="/comments/comment[@id='D3D12_RESOURCE_FLAG_NONE']/*"/>	
        /// <msdn-id>dn986742</msdn-id>	
        /// <unmanaged>D3D12_RESOURCE_FLAG_NONE</unmanaged>	
        /// <unmanaged-short>D3D12_RESOURCE_FLAG_NONE</unmanaged-short>	
